try {
    var slideoutin = j5.slideoutin(document.getElementById("menu"), "l", 0, myexe);

    function myexe(pheight) {
        pheight = pheight - 45;
        document.getElementById("memu_list").style.height = pheight + "px"
    };
    var sliderobj2 = document.getElementById("slider2");
    var sliderdata2 = j5.getBoxData(sliderobj2);
    var width2 = sliderdata2.curWidth;
    var arr2 = init_slider(sliderobj2, ".year_box", width2, true);
    sliderobj2.style.height = arr2[0] + "px";
    var j5obj2 = j5.touchBxSlider({
        slider: sliderobj2,
        pagertime: 5000,
        pagerwidth: width2,
        boxHarray: arr2,
		pagerlength: 5,
        func: tabinit2
    });
    var tabs2 = gettabs($("#slider2_tab li"));
    tabinit2(0, true);

    function tabinit(id, isfirst) {
        for (var ii = 0; ii < tabs.length; ii++) {
            var obj = tabs[ii];
            obj.attr("class", "");
            if (id == ii) {
                obj.attr("class", "tabnow")
            };
            if (isfirst) {
                tabclick(obj, ii)
            }
        }
    };

    function tabclick(obj, ii) {
        obj.click(function () {
            tabinit(ii, false);
            j5obj.slideToIndex(ii)
        })
    };

    function tabinit2(id, isfirst) {
        for (var ii = 0; ii < tabs2.length; ii++) {
            var obj = tabs2[ii];
            obj.attr("class", "");
            if (id == ii) {
                obj.attr("class", "tabnow")
            };
            if (isfirst) {
                tabclick2(obj, ii)
            }
        }
    };
	

    function tabclick2(obj, ii) {
        obj.click(function () {
            tabinit2(ii, false);
            j5obj2.slideToIndex(ii)
        })
    };
	

    function gettabs(tabarr) {
        var _tabarr = new Array();
        for (var ii = 0; ii < tabarr.length; ii++) {
            var obj = $(tabarr[ii]);
            _tabarr.push(obj)
        };
        return _tabarr
    };

    function pointclick(ii) {
        tabs2[ii].click()
    };

    function init_slider(domobj, findfilter, _width, isloop) {
        var boxs = $(domobj).find(findfilter);
        var arrHdata = new Array();
        for (var i = 0; i < boxs.length; i++) {
            var left = i * _width;
            $(boxs[i]).css("left", left + "px");
            var boxobj = j5.getBoxData(boxs[i]);
            arrHdata.push(boxobj.curHeight);
            if (isloop) {
                if (i == 0) {
                    var obj = $(boxs[i]).clone();
                    obj.css("left", boxs.length * _width + "px");
                    $(domobj).append(obj)
                };
                if (i == boxs.length - 1) {
                    var obj = $(boxs[i]).clone();
                    obj.css("left", -1 * _width + "px");
                    $(domobj).append(obj)
                }
            }
        };
        return arrHdata
    }
} catch (e) {}